<?php header('Access-Control-Allow-Origin: *'); ?>  <?php	$conn =oci_connect('dbo','dbo','(DESCRIPTION=    (ADDRESS=      (PROTOCOL=TCP)      (HOST=10.10.180.21)      (PORT=1522)    )    (CONNECT_DATA=      (SERVER=default)      (SERVICE_NAME=AAVEPROD)    )  )');        		switch ($_REQUEST['ot']){		case "1":			$result= oci_parse($conn,"SELECT CODVEHICULOMARCA as codigo, DESCVEHICULOMARCA as marca FROM SG_VEHICULOMARCA as marcas WHERE ESTADO = 'A' and CODVEHICULOMARCA <> 0 ORDER BY DESCVEHICULOMARCA ASC;");			while($row=oci_fetch_array($result))			{				echo "<option value=" . $row['codigo'] . ">" . $row['marca'] . "</option>";			}		break;		case "2":			$result=oci_parse($conn,"SELECT CODVEHICULOGRUPOMODELO AS codigo, DESCVEHICULOGRUPOMODELO AS modelo FROM SG_VEHICULOGRUPOMODELO WHERE CODVEHICULOMARCA =  " . $_REQUEST['ma'] . " ORDER BY DESCVEHICULOGRUPOMODELO ASC;");			while($row=oci_fetch_array($result))			{				echo "<option value=" . $row['codigo'] . ">" . $row['modelo'] . "</option>";			}		break;		case "3":			$result=oci_parse($conn,"SELECT IDVEHICULO AS codigo, DESCVEHICULO AS version FROM SG_VEHICULO WHERE CODVEHICULOMARCA =  " . $_REQUEST['ma'] . " AND ESTADO =  'A' AND CODVEHICULOGRUPOMODELO =  " . $_REQUEST['mo'] . " ORDER BY DESCVEHICULO ASC");			while($row=oci_fetch_array($result))			{ 				echo "<option value=" . $row['codigo'] . ">" . $row['version'] . "</option>";			}		break;			case "4":			$year = date("Y");			$result=oci_parse($conn,"SELECT * FROM SG_VEHICULOVALOR WHERE IDVEHICULO =" . $_REQUEST['co'] . "");			$row=oci_fetch_array($result);			for($i=1;$i<33;$i++)			{					if($row[$i] > 0)				{					$ano = $year-$i + 2;					echo "<option value=" . $row[$i] . ">" . $ano ."</option>" . "<br />";				}			}		break;	} ?>